import React from "react";

import logo from "../../Images/BITlogo.png";

// import useNavigation from "use-navigation";
import { useNavigate } from "react-router";
import { useState, useContext } from "react";
import UserContext from "../../context/UserContext";

import {GoThreeBars} from "react-icons/go";
import { useEffect } from "react";

const Navbar = () => {
  const navigate = useNavigate();
  const user = useContext(UserContext);


  // manipulation by dom elements 
  const button = document.getElementById("menuButton");
  // manipulation by dom elements 
  
  const [clicked , setClicked] = useState(false);

  
  function checkMediaQuery() {
    // If the inner width of the window is greater then 768px
    if (window.innerWidth > 768) {
      // Then log this message to the console
      console.log('Media Query Matched!')
    }
  }
  
  // Add a listener for when the window resizes
  window.addEventListener('resize', checkMediaQuery);
  
  const showMenu = (e) => {
    var navButtons = document.getElementById("navButtons");
    setClicked(!clicked)
    if(!clicked){
      // navButtons.classList.add("toggleClass");
      navButtons.style.display = "block";
      setClicked(true)
      navButtons.style.display = "flex";
    }    
    else{
      navButtons.style.display = "none"
    }
  }
  


  const [media , setMedia] = useState(true);

  const shortAccount = () => {
    let accountLength = user.userAccount.length;
    let firstPart = user.userAccount.slice(0, 5);
    let lastPart = user.userAccount.slice(accountLength - 5, accountLength - 1);
    return firstPart + "..." + lastPart;
  };

 


  return (
    <>
      <div className="login_nav">
        <div className="logo">
          <img
            onClick={() => {
              navigate("/");
            }}
            src={logo}
            alt="Beyong Imagination Technologies"
          />
        </div>

        <div className="nav_buttons" id="navButtons">
{/* {
  media ? <> */}


        {user.isConnected && (
          <>
            <div className="createButton">
              <button
                onClick={() => {
                  if (user.isConnected) {
                    navigate("/create");
                  }
                }}
              >
                Create
              </button>
            </div>
            <div className="clubButton">
              <button
                onClick={() => {
                  if (user.isConnected) {
                    navigate("/clubs");
                  }
                }}
              >
                Clubs
              </button>
            </div>
          </>
        )}

        <div className="connect_button">
          <button
            onClick={() => {
              if (user.isConnected) {
                navigate("/profile");
              } else {
                user.login();
              }
            }}
          >
            {user.isConnected ? shortAccount() : "Connect"}
          </button>         
        </div>

  {/* </> : <>
    <div>
        <button></button>
    </div>
  </>
        
      } */}

        </div>

        <div className="viewOnSmallDevice" id="menuButton" onClick={showMenu}>
          {!clicked ?  <GoThreeBars  /> : "X"}
        </div>

      </div>
    </>
  );
};

export default Navbar;
