import React from "react";
import "../components/Login.css";
const ExploreMore = () => {
  return (
    <>
      <div className="explore">
        <div className="inner">
          <h2>What is Web3 ? </h2>
          <div className="content">
            <p>
               
              <br></br><br></br>
              Web3 is decentralized: instead of large swathes of the internet controlled
              and owned by centralized entities, ownership gets distributed
              amongst its builders and users. <br></br> <br></br><br></br> Web3 is permissionless: everyone
              has equal access to participate in Web3, and no one gets excluded.<br></br><br></br><br></br>
              Web3 has native payments: it uses cryptocurrency for spending and
              sending money online instead of relying on the outdated
              infrastructure of banks and payment processors.<br></br><br></br><br></br>  Web3 is trustless:
              it operates using incentives and economic mechanisms instead of
              relying on trusted third-parties.
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default ExploreMore;
