import React from "react";
import Navbar from "../Navbar/Navbar";
import "./Create.css";
import CreateScript from "./CreateScript";
const Create = () => {
  const {
    status,
    setStatus,
    setUploadedImage,
    setClubName,
    setClubDescription,
    setClubCategory,
    handleCreateButton,
  } = CreateScript();

  return (
    <>
      <div className="outerCreate">
      <div className="createbox">
        <div className="heading">
          <h2>Create a Club</h2>
        </div>
        <div className="create_form">
          <form>
            <div className="photo">
              <div className="file-input">
                <input
                  type="file"
                  id="file"
                  className="file"
                  onChange={(e) => {
                    setUploadedImage(e.target.files[0]);
                  }}
                />
                <label htmlFor="file">Select file</label>
              </div>
            </div>
            <div className="clubName">
              <input
                type="text"
                placeholder="Club Name"
                id="clubName"
                onChange={(e) => {
                  setClubName(e.target.value);
                }}
              />
            </div>
            <div className="clubDescription">
              {/* <textarea  /> */}
              <textarea
                type="text"
                placeholder="Club Description"
                name=""
                id="clubDescription"
                cols="93"
                rows="10"
                onChange={(e) => {
                  setClubDescription(e.target.value);
                }}
              ></textarea>
            </div>
            {/* <div className="clubAddress">
              <input type="text" placeholder="Contract Address" />
            </div> */}
            <div className="category">
              <p>Category</p>
              <select
                name=""
                id="clubCategory"
                onChange={(e) => {
                  console.log(e.target.value);
                  setClubCategory(e.target.value);
                }}
              >
                <option value="üåê Web3">üåê Web3</option>
                <option value="ü™ê Metaverse">ü™ê Metaverse</option>
                <option value="üè¶ DeFi">üè¶ DeFi</option>
                <option value="üéÆ GameFi">üéÆ GameFi</option>
                <option value="üíñ SocialFi">üíñ SocialFi</option>
                <option value="üñº NFT">üñº NFT</option>
                <option value="üó≥Ô∏è DAO">üó≥Ô∏è DAO</option>
                <option value="‚ö° Layer2">‚ö° Layer2</option>
                <option value="üîê Crypto">üîê Crypto</option>
                <option value="üí© Meme">üí© Meme</option>
                <option value="üè∑Ô∏è Others">üè∑Ô∏è Others</option>
              </select>
            </div>
            <div className="status">{status}</div>
            <div className="create_button">
              <button
                onClick={(e) => {
                  handleCreateButton(e);
                }}
              >
                Create
              </button>
            </div>
          </form>
        </div>
      </div>
      </div>

    </>
  );
};

export default Create;
